#define cbrt(x) sign(x) * pow(abs(x), 1. / 3.)

float sdEllipse(vec2 p, vec2 ab)
{
    p = abs(p); if(p.x > p.y) { p = p.yx; ab = ab.yx; }

    float l = ab.y * ab.y - ab.x * ab.x,
          m = ab.x * p.x / l,      m2 = m * m,
          n = ab.y * p.y / l,      n2 = n * n,
          c = (m2 + n2 - 1.) / 3., c3 = c * c * c,
          q = c3 + m2 * n2 * 2.,
          d = c3 + m2 * n2,
          g = m + m * n2, co;

    if(d < 0.)
    {
        float h = acos(q / c3) / 3.,
              s = cos(h) + 2.,
              t = sin(h) * sqrt(3.);

        vec2 r = sqrt(m2 - c * vec2(s + t, s - t));

        co = .5 * (r.y + sign(l) * r.x + abs(g) / r.x / r.y - m);
    }
    else
    {
        float h = 2. * m * n * sqrt(d),
              s = cbrt(q + h),
              u = cbrt(q - h),
              rx = 2. * m2 - s - u - c * 4.,
              ry = (s - u) * sqrt(3.),
              rm = sqrt(rx * rx + ry * ry);

        co = .5 * (ry / sqrt(rm - rx) - m) + g / rm;
    }

    vec2 r = ab * vec2(co, sqrt(1. - co * co));

    return length(r - p) * sign(p.y - r.y);
}

float sdMoon(vec2 p, float d, float ra, float rb)
{
    p.x = abs(p.x);

    float a = .5 * (ra * ra - rb * rb + d * d) / d,
          b = sqrt(max(ra * ra - a * a, 0.));

    return p.y * b - p.x * a > d * max(b - p.x, 0.) ? length(p - vec2(b, a))
                                                    : max(length(p) - ra, rb - length(p - vec2(0, d)));
}

float face(vec2 p)
{
    p.x = abs(p.x);

    return min(sdEllipse(p - vec2(.15, .095), vec2(.05, .075)),
               sdMoon(p - vec2(0, .03), .116, .28, .37) - .005);
}

#define AA(d) smoothstep(-1., 1., (d) / length(vec2(dFdx(d), dFdy(d))))

vec3 screen(vec3 a, vec3 b)
{
    return a + b - a * b;
}

vec3 smile(vec2 p)
{
    vec2 q = p -= .5, r = p;
         q.y += .02;
         r.y -= .01;

    vec3 a = vec3(1.03318,  1.0115, .269633),
         b = vec3(1.42421, .666447, 1.23014),
         c = vec3(13.7743, 1.01125, 2.90418),
         col = clamp(a - .1 / pow(b - length(p), c), 0., 1.);

    col = mix(col, vec3(1), max(vec3(2.4, 3, 2) * p.y - vec3(.07, -.1, .09), 0.) * AA(.455 - length(p)));

    col = screen(col, vec3(pow(col.x, 2.6) * 1.5 * smoothstep(.8, .5, length(p - vec2(0, .45))),
                           pow(col.x, 1.4) * .9 * smoothstep(.9, 0., length(p - vec2(0, .45))),
                           (.8 * col.x - .6) * 1.8 * smoothstep(.3, 0., length(p - vec2(0, .45)))));

    col = mix(col, vec3(1,0.933,0.404), smoothstep(.01, -.02, face(q)));

    col = mix(col, vec3(0.827,0.639,0.051), smoothstep(.01, -.02, face(r)));

    col = mix(mix(vec3(0.643,0.396,0.027), vec3(0.369,0.161,0.008), smoothstep(-.037, .027, face(q))),
              col, AA(face(p)));

    if (p.y < 0.) col = mix(vec3(0.408,0.196,0.012), col, AA(face(p)));

    return col * AA(.5 - length(p));
}

void mainImage(out vec4 O, vec2 I)
{
    vec2 uv = pow(5., 1. - min(.2 * iTime, .9)) * (I - .5 * iResolution.xy) / iResolution.y + .5;
    O = vec4(smile(uv), 1);
