void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    float k = 10.0 + iTime * 10.0; 
    float x = uv.x * 3.0; 
    float y = uv.y * 3.0; 

    float heartY = pow(abs(x), 2.0 / 3.0) + 0.9 * sin(k * x) * sqrt(max(0.0, 3.0 - x * x));

    float d = abs(y - heartY);

    float lineThickness = 0.02;
    if (d < lineThickness) {
        fragColor = vec4(1.0, 0.2, 0.3, 1.0);
    } else {
        fragColor = vec4(0.0);
    }
}
