#define pi 3.14159

float tex(vec2 pix){return texture(iChannel0,pix/iResolution.xy).r*(iResolution.y/2.);}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float xSlope=tex(fragCoord)-tex(fragCoord-vec2(1,0));
    float ySlope=tex(fragCoord)-tex(fragCoord-vec2(0,1));

    float angle = atan(ySlope/xSlope)+pi*float(bool(xSlope>0.));

    float strength=1.-(1./(abs(xSlope+ySlope)/2.+1.));

    float lightAngle=iTime;

    vec3 col = strength*vec3(max(0.,cos(angle-lightAngle)));

    fragColor = vec4(col,1.0);
}
