#define S(v) smoothstep(20./R.y,0., v)     // AA draw

vec2 D = vec2(1.732,1);

float T(vec2 U, float a) {                 // --- tear-shape tile SDF
    U *= mat2(cos( a - vec4(0,33,11,0)));  // local frame → to peak direction
    U.y = abs(U.y);                        // tear symmetry
    float l = length(U);
 // return atan(U.y,U.x) > 3.14/6. || dot(U,U) > 3. 
    return U.x/l < .867 || l*l > 3. 
      ? l  - 1.                            // circle part
      : 1. - length( U - D );              // peak part
}
// note: we could either avoid the rotation by adjusting peak angle + D offset
// or avoid the atan by just considering cos = U.x/length < sqrt(3)/2


void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = 4.* ( u+u - R ) / R.y,
         E = vec2(-D.x,D.y), J = vec2(0,2);

    float d = min( min( min( T(U  ,0.)  , T(U-D-D+J,3.14) ),   // center & E
                        min( T(U+D,0.)  , T(U-D,3.14 ) )  ),   // SW & NE
                   min( min( T(U+E,0.)  , T(U-E,-1.05) ),      // SE & NW
                        min( T(U+J,1.05), T(U-J,-2.1 ) ) ));   //  S & N                   

    O = vec4( max( S( abs(d) ) ,           // outline
                   .5 * S(d) ) );          // fill

}
