// respin of spalmer https://www.shadertoy.com/view/XcfGDr
// respin of FabriceNeyret2 http://shadertoy.com/view/XfX3Dn

void mainImage(out vec4 O, vec2 u)
{
    vec2 R = iResolution.xy,
         U = 1.2 * (u + u - R) / R.y;
    O *= 0.;
    for (float i; i++ < 9.; )
        O = max(O, smoothstep(5./R.y*i, 0., 
                       abs(length(  vec2(
                               mod( iTime + U.x*i + sign(U.y) , 4.) - 2.
                             , U.y*i
                                  )     )
                        - 1.
                          )   ) 
                  / pow(1.2, i)
                );
}
