const float TAU = 6.28318;

vec3 palette(in float t)
{

    // vec3 a = vec3(0.138, 0.189, 0.761); vec3 b = vec3(0.448, 0.797, 0.568); vec3 c = vec3(0.591, 1.568, 0.065); vec3 d = vec3(4.347, 2.915, 0.976);

    vec3 a = vec3(0.000, 0.500, 0.500);
    vec3 b = vec3(2.000, 0.500, 0.490);
    vec3 c = vec3(2.000, 2.000, 0.500);
    vec3 d = vec3(0.000, 0.667, 0.500);

    return a + b * cos( TAU * (c * t + d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2. - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.);

    for (float i = 0.; i < 3.; i++) {
        uv = (fract(6. * uv * pow(0.125, i)) - .5);

        float d = length(uv) * exp(-length(uv0));

        vec3 col = palette(length(uv0) + i *.4 + iTime * pow(0.4, i));

        d = sin(d * 8. + iTime) / 8.;

        d = abs(d);

        d = pow(0.01 / d, 3.);

        finalColor += col * d;

    }

    fragColor = vec4(finalColor, 1.0);
}
