// truchet patterns designed for wallpaper
// old school style hope u like.

#define S smoothstep
#define R min(I.x, I.y)
#define I iResolution.xy
#define C vec3 c; c += .5
#define d( p, l ) S( l / R, 0., k( p ) )
#define rot(a) mat2( cos(a), sin(a), -sin(a), cos(a) )
#define k( p ) min( min( abs(p-.02), abs(p+.02) ), abs(p) )
#define p vec2 u = U + U - I;u/=R;u -= iTime * .1;u = mod( u, 5. ) - 2.5
#define h( u ) fract( dot( u.yxx * .1234, sign( u.yxx ) + 1213.456 ) * 123.456 )

void mainImage( out vec4 O, in vec2 U )
{
    p;
    C;

    float r = h( abs( round( u / -.2 ) - 20. ) );
    u = mod( u + .1, .2 ) - .1, 
    u *= rot( round( r ) * 1.57 ),
    u += sign( u.x + u.y ) * -.1;

    c = mix(
        c,
        vec3(1),
        d( ( length( u ) - .1 ), 4. ) 
        * 1. - ( S( 4. / R, 0., abs(length(u) - .1 + step( r, .5 ) * .02 ) )
        * step( fract( atan( u.x, u.y ) / 3.14  - fract(r - iTime * 2. * sign( r ) * r - .1 ) ), fract( r ) )  )
    );


    O = vec4(c,1.0);
}
