float rand (in vec2 uv)
{
    return fract(sin(uv.x*123.53+uv.y*42.194+53.2)*39.42);
}

float noise (vec2 uv)
{
    vec2 i = floor(uv);
    vec2 f = fract(uv);

    float a = rand(i);
    float b = rand(i+vec2(1.,0.));
    float c = rand(i+vec2(0.,1.));
    float d = rand(i+vec2(1.));

    vec2 u = f*f*(3.-2.*f);

    return mix(a, b, u.x) +
    (c-a)*u.y*(1.0-u.x)+
    (d-b)*u.x*u.y;
}

vec2 getPos(in vec2 uv)
{
    float p = 32., pSize = 1./p, mSize = 2., tot = 1.;
    vec2 r = vec2(0), s = uv;

    for (float i = 0.; i < tot; i++)
    {
        vec2 t = s;
        t *= p;
        t = floor(t);
        t *= pSize;

        r += t;
        s -= t;

        tot = noise(r*10.+iTime)*8.2+1.;

        p *= 1.3;
        pSize *= .8;

    }
    /*

    p = 32

    *8.2

    p *= 1.3
    pSize *= .8

    */


    return r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;

    uv = getPos(uv);

    // Time varying pixel color
    vec3 col = texture(iChannel0,uv).xyz;//0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    //col = vec3(0,col.y,0);

    // Output to screen
    fragColor = vec4(col,1.0);
}
