float blinnWyvillCosineApproximation(float x) {
    float x2 = x*x;
    float x4 = x2*x2;
    float x6 = x4*x2;

    float fa = ( 4.0/9.0);
    float fb = (17.0/9.0);
    float fc = (22.0/9.0);

    float y = fa*x6 - fb*x4 + fc*x2;

    return y;
}

float doubleCubicSeat(float x, float a, float b) {
    float epsilon = 0.00001;
    float min_param_a = 0.0 + epsilon;
    float max_param_a = 1.0 - epsilon;
    float min_param_b = 0.0;
    float max_param_b = 1.0;

    a = min(max_param_a, max(min_param_a, a));  
    b = min(max_param_b, max(min_param_b, b)); 

    float y = 0.0;

    if (x <= a){
        y = b - b*pow(1.0-x/a, 3.0);
    } else {
        y = b + (1.0-b)*pow((x-a)/(1.0-a), 3.0);
    }

    return y;
}

float doubleCubicSeatWithLinearBlend(float x, float a, float b) {
    float epsilon = 0.00001;
    float min_param_a = 0.0 + epsilon;
    float max_param_a = 1.0 - epsilon;
    float min_param_b = 0.0;
    float max_param_b = 1.0;

    a = min(max_param_a, max(min_param_a, a));  
    b = min(max_param_b, max(min_param_b, b)); 
    b = 1.0 - b; //reverse for intelligibility.

    float y = 0.0;

    if (x<=a){
        y = b*x + (1.0-b)*a*(1.0-pow(1.0-x/a, 3.0));
    } else {
        y = b*x + (1.0-b)*(a + (1.0-a)*pow((x-a)/(1.0-a), 3.0));
    }
    return y;
}

float doubleOddPolynomialSeat(float x, float a, float b, int n) {
    float epsilon = 0.00001;
    float min_param_a = 0.0 + epsilon;
    float max_param_a = 1.0 - epsilon;
    float min_param_b = 0.0;
    float max_param_b = 1.0;

    a = min(max_param_a, max(min_param_a, a));  
    b = min(max_param_b, max(min_param_b, b)); 

    int p = 2 * n + 1;
    float y = 0.0;

    if (x <= a){
        y = b - b*pow(1.0-x/a, float(p));
    } else {
        y = b + (1.0-b)*pow((x-a)/(1.0-a), float(p));
    }
    return y;
}

float doublePolynomialSigmoid(float x, float a, float b, int n){
    float y = 0.0;

    if (n%2 == 0){
        // even polynomial
        if (x<=0.5){
            y = pow(2.0*x, float(n))/2.0;
        } else {
            y = 1.0 - pow(2.0*(x-1.0), float(n))/2.0;
        }
    }
    else { 
        // odd polynomial
        if (x <= 0.5){
            y = pow(2.0*x, float(n))/2.0;
        } else {
            y = 1.0 + pow(2.0*(x-1.0), float(n))/2.0;
        }
    }

    return y;
}

float quadraticThroughAGivenPoint(float x, float a, float b){
    float epsilon = 0.00001;
    float min_param_a = 0.0 + epsilon;
    float max_param_a = 1.0 - epsilon;
    float min_param_b = 0.0;
    float max_param_b = 1.0;

    a = min(max_param_a, max(min_param_a, a));  
    b = min(max_param_b, max(min_param_b, b)); 

    float A = (1.0-b)/(1.0-a) - (b/a);
    float B = (A*(a*a)-b)/a;
    float y = A*(x*x) - B*(x);

    y = min(1.0,max(0.0,y)); 

    return y;
}

float plot(vec2 coord, float value){
  return  smoothstep( value-0.02, value, coord.y) -
          smoothstep( value, value+0.02, coord.y);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy / iResolution.xy;

    // gradient
    float y = uv.x;
    vec3 color = vec3(y);

    // line plot
    float line = plot(uv, blinnWyvillCosineApproximation(uv.x));
    //float line = plot(uv, doubleCubicSeat(uv.x, 0.407, 0.720));
    //float line = plot(uv, doubleCubicSeatWithLinearBlend(uv.x, 0.640, 0.827));
    //float line = plot(uv, doubleOddPolynomialSeat(uv.x, 0.520, 0.613, 15)); // n=[1, 20)
    //float line = plot(uv, doublePolynomialSigmoid(uv.x, 0.520, 0.613, 5)); // n=[1, 10)
    //float line = plot(uv, quadraticThroughAGivenPoint(uv.x, 0.233, 0.340));

    // overlay line onto gradient
    color = (1.0 - line) * color + line * vec3(0.0, 1.0, 0.0);

    fragColor = vec4(color, 1.0);
}
