vec3 palette(float t) {
    vec3 a = vec3(0.383, 0.875, 0.831);
    vec3 b = vec3(0.313, 0.376, 0.088);
    vec3 c = vec3(0.695, 1.000, 0.200);
    vec3 d = vec3(2.063, 1.803, 0.546);    

    return a + b * cos(6.28318 * (c * t + d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 ov = uv;
    vec3 finalCol = vec3(0.0);

    for(int i = 0; i < 3; i++) {
        uv = fract(uv * 0.75) - 0.5;

        float d = length(uv) * exp(length(ov));

        vec3 col = palette(length(uv) + float(i) + iTime * 0.5);

        d = sin(d * 4.0 + iTime * 2.0) / 4.0;
        d = abs(d);

        d = pow(0.05 / d, 1.2);

        finalCol += col * d;
    }

    fragColor = vec4(finalCol, 1.0);
}
