float col,a,x,s,y,b,t,w,h,f,z,l=80.; //l is # of iterations



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    for (f=0.;f<l;f++){
    a = iTime/50.   ; //neck hurt
    w = iResolution.x;
    h = iResolution.y;
    t = w/2.+(fragCoord.x+fragCoord.y*iResolution.x);
    z = (h-fragCoord.y)/((1000.-sin(iTime/15.)*980.)-(s*1200.));// (screen size)/(cam height-terrain height)

    x = iTime+(
          sin(a     )*(mod(t,  w)-w/2. )+ 
          cos(a     )*(mod(t/w,w)+1000.) 
        )/z;

    y = (
          cos(a     )*(mod(t,  w)-w/2. )+
          sin(a+3.14)*(mod(t/w,w)+1000.) 
        )/z;
    b = 1.-sin(iTime/20.);
    s = abs(                                               //main signal, "texture"    
          (sin(x/250.+sin(iTime/10.)*5.+sin(x/38.+sin(x/10.+sin(x/700.+sin(x/11.)*b)*b)*b)*b))/z+
          (cos(y/250.+cos(iTime/10.)*5.+cos(y/38.+cos(y/10.+cos(y/700.+cos(y/11.)*b)*b)*b)*b))/z
         )/5.;
    col += float(z>0.)*(float(s>0.)*float(s<0.03)*s*150./l);
    }


    // Output to screen
    fragColor = vec4(col*0.4,col*0.6,col*0.5,1.);
}
