#define hash_f_s(s)  ( float( hashi(uint(s)) ) / float( 0xffffffffU ) )
#define hash_f()  ( float( seed = hashi(seed) ) / float( 0xffffffffU ) )
#define hash_v2()  vec2(hash_f(),hash_f())
#define hash_v3()  vec3(hash_f(),hash_f(),hash_f())
#define hash_v4()  vec3(hash_f(),hash_f(),hash_f(),hash_f())

uint seed;
uint hashi(uint x) {
    x ^= x >> 16;
    x *= 0x7feb352dU;
    x ^= x >> 15;
    x *= 0x846ca68bU;
    x ^= x >> 16;
    return x;
}



float sdSegment(vec2 p, vec2 a, vec2 b) {
    vec2 pa = p - a, ba = b - a;
    float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
    return length(pa - ba * h);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {    
    vec2 uv = fragCoord/ iResolution.y;
    ivec2 i_coords = ivec2(fragCoord);        
    int id = i_coords.x + i_coords.y * int(iResolution.x);
    seed = 1235125u + uint(id);   
    vec3 col = vec3(0.0); // Start with black background     
    vec2 oguv  = uv;    
    float numLines = 5.0;
    uv *= numLines;
    uv.y = fract(uv.y) - .5;
    uv.x -= numLines;
    float lineThickness = abs(oguv.y) * .1;    
    float line = sdSegment(uv, vec2(-numLines + 2.0, 0.0), vec2(numLines - 3.0, 0.0)) - lineThickness;
    col += smoothstep(-0.01, 0.01, -line) * vec3(hash_f()); 
    fragColor = vec4(col, 1.0);
}
