#define pi 3.1415926535
#define t iTime
void mainImage( out vec4 o, in vec2 f )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = f/iResolution.xy;
    float c1=0.;
    float c2=0.;

    c1 = sin((uv.x-t)*(5.+t/10.)*pi) + sin((-(uv.x+t))*(5.+t/10.)*pi);
    //c2 = sin(2.*t) * sin((uv.y)*5.*pi);

    float c=c1+c2;
    o=vec4(clamp(c,0.,1.),0.,-clamp(c,-1.,0.),1.);

}
