// changed from https://www.shadertoy.com/view/3tlXWS (by artemis)


#define FALLOFF_START 1.2 // 2.0
#define DECAY 1.1

float sRGB(float x) {
    if (x <= 0.00031308)
        return 12.92 * x;
    else
        return 1.055*pow(x,(1.0 / 2.4) ) - 0.055;
}

float electron_beam(vec2 pix, float t) {


    float beam_x = (cos(t * 1.0) * 250.0 + 1.0) / 2.0;
    float beam_y = 0.5f;

    float dist = distance(pix, vec2(beam_x, beam_y));

    float power = 1.0 - (min( 5.13 -t, DECAY) / DECAY);

    float falloff = power * FALLOFF_START;

    return min(power, power * (falloff * falloff) / (dist * dist));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 center = iResolution.xy / 2.0;
    float scale = 512.0 / iResolution.x * .4;
    vec2 uv = (fragCoord - center) * scale;

    float beam = 0.0;
    float t = 0.0;
    for (int i = 0; i < 500; i++) {
        beam += electron_beam(uv, 5.13 - t);
        t += 0.0025;
    }

    vec3 beam_col = vec3(sRGB(beam), sRGB(beam* 0.3), sRGB(beam));

    fragColor = vec4(beam_col, 1.0);
}
