vec2 square(vec2 i){return vec2(i.x*i.x-i.y*i.y, 2.*i.x*i.y);}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int steps = 200;
    vec2 uv = fragCoord/iResolution.xy;
    uv *= 3.;
    uv.x -= 2.;
    uv.y -= 1.5;
    uv.x *= iResolution.x/iResolution.y;
    uv = uv/pow(2.,iTime);

    vec2 z = vec2(0.);
    float depth = 0.;
    for(int i = 0; i<steps; i++){
        z = square(z) + uv - vec2(1.4,0.001);
        if(length(z)>2000.){break;}
        depth++;
    }
    depth = float(steps) - depth;
    vec3 col = vec3(depth/float(steps));
    fragColor = vec4(col,1.0);
}
