#define rotation(angle) mat2(cos(angle), -sin(angle), sin(angle), cos(angle));

// IQ Hexagon SDF
float sdHexagon( in vec2 p, in float r )
{
    const vec3 k = vec3(-0.866025404,0.5,0.577350269);
    p = abs(p);
    p -= 2.0*min(dot(k.xy,p),0.0)*k.xy;
    p -= vec2(clamp(p.x, -k.z*r, k.z*r), r);
    return length(p)*sign(p.y);
}

float PI = 3.14159;
float TAU = 2.*3.14159;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 uv = ( fragCoord - .5* iResolution.xy ) /iResolution.y;
   float a = (TAU)*pow(1.-length(uv),10.);
   uv *= rotation(PI/3. + a*a/2.); 
   vec3 col = vec3(0.);
   float hexSDF = sdHexagon(uv, .3);
   hexSDF = abs(sin(-iTime + 2.*TAU*hexSDF)/6.);
   col += pow(.05/hexSDF,1.8);
   fragColor = vec4(col,1.0);
}
