void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoom = 4.;
    fragCoord /= zoom;

    vec3 col;
    if (iFrame < 2) col = vec3(1);
    else col = texelFetch(iChannel0, ivec2(fragCoord), 0).xyz;
    col = clamp(col,0.,1.);
    fragColor = vec4(col, 1.0);
}

#define prev(X,Y) ( texelFetch(iChannel0, ivec2(fragCoord)+ivec2(X,Y), 0) )
#define noise(X,Y) ( texture(iChannel1, fract(uv+vec2(X,Y))) )

#define WHITE vec4(1.,1.,1.,0.)
#define BLACK vec4(0.,0.,0.,0.)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;

    vec4 col;

    // Initial condition
    if (iFrame < 5) {
        //col = WHITE*step(0., fragCoord.x - iResolution.x/2.);
        //col = WHITE*step(length(round(fragCoord - iResolution.xy*0.5)),50.);
        col = vec4(noise(0,0).x,noise(0.25,0).x,noise(0,0.5).x,0);
    } else {
        col = prev(0,0);
        int iterationEachNFrames = 2;
        if (iFrame % iterationEachNFrames == 0) {
            vec4 neigh = prev(1,0)+prev(1,1)+prev(0,1)+prev(-1,1)+prev(-1,0)+prev(-1,-1)+prev(0,-1)+prev(1,-1);
            col = step(vec4(0.5), col)*step(vec4(1.5),neigh)*step(neigh,vec4(3.5)) + step(col,vec4(0.5))*step(vec4(2.5),neigh)*step(neigh,vec4(3.5));
        }
    }

    fragColor = col;
}
