vec2 rotateAxisX(vec2 uv, float angle) {
    uv -= 0.5;
    float r = radians(angle);
    vec3 ret = inverse(mat3( 1,0,0,  0,cos(r),sin(r), 0,0,2 ) ) * vec3(uv, 2.0);
    return ret.xy / ret.z + 0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord / iResolution.xy;
    vec2 id = floor(uv);
    vec2 localUv = fract(uv);

    float progress = mod(iTime*0.1, 1.0);
    float angle = progress*180.0;
    uv = rotateAxisX(localUv, angle);
    float flip = 90.0 < angle && angle < 270.0 ? 1.0 : 0.0;
    if (flip > 0.0) {
        uv.y = 1.0 - uv.y;
    }    
    vec3 col = vec3(0.0);
    if (0.0 <= uv.x && uv.x <= 1.0 && 0.0 <= uv.y && uv.y <= 1.0) {
        col = mix(texture(iChannel1, uv).rgb, texture(iChannel2, uv).rgb, flip);
    }

    fragColor = vec4(col, 1.0);
}
