// Fork of "Kis-Snowflakes Trippy2 233c" by ttoinou. https://shadertoy.com/view/wllSDs
// 2024-01-27 16:24:03

void mainImage(out vec4 z, vec2 u) {
    vec2 A, B, C, G;
    B.x = ++C.y + .7;
    for(
        float a, k;
        k < 99.; // change here to increase zoom level
        z = cos( iTime + vec4(9,5,7,1)*k ) - sin(iTime-a+k)
    )
        G = A+B,
        a = dot( normalize(u/iResolution.y-G), normalize(B+B-A) ),
        A = a > -.5 ? k++, B + C : A,
        B = a >  .5 ? k++, B : C,
        C = G/3.,
        k *= 3.;
}
