float square(float x1, float x2, float y1, float y2, vec2 uv)
{
    return step(x1,uv.x)*step(uv.x,x2)*step(y1,uv.y)*step(uv.y,y2);
}

float smoothSquare(float x1, float x2, float y1, float y2, vec2 uv, float s)
{
    float sx = smoothstep(x1-s,x1+s,uv.x)*(1.0-smoothstep(x2-s,x2+s,uv.x));
    float sy = smoothstep(y1-s,y1+s,uv.y)*(1.0-smoothstep(y2-s,y2+s,uv.y));

    return sx*sy;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    float rgb = 255.0;
    vec3 white = vec3(245,245,244)/rgb;
    vec3 red = vec3(255,60,45)/rgb;
    vec3 yellow = vec3(232,194,55)/rgb;
    vec3 blue = vec3(47,83,135)/rgb;
    vec3 black = vec3(25,24,24)/rgb;

    float t1 = 0.85+0.15*sin(iTime/1.7);
    float t2 = 0.90+0.1*sin(iTime/2.0);
    float t3 = 0.93+0.07*sin(iTime/2.3);

    float cs = 0.5;
    float lt = 0.02;
    float lt2 = lt+0.0025;

    float h1 = 0.33, h2 = h1-lt2, h3 = -0.42, h4 = h3-lt2;
    float v1 = -0.31, v2 = v1+lt;
    float s = 0.002;

    vec3 bg = black*step(uv.x,cs)*step(-cs,uv.x);
    //vec3 r1 = red*square(-cs,v1,h1,cs,uv);
    //vec3 y1 = yellow*square(-cs,v1,h3,-0.23,uv);
    //vec3 b1 = blue*square(0.13,cs,-cs,h4,uv);

    //vec3 w1 = white*square(-cs,v1,-0.21,h2,uv);
    //vec3 w2 = white*square(-cs,v1,-cs,h4,uv);
    //vec3 w3 = white*square(v2,cs,h1,cs,uv);
    //vec3 w4 = white*square(v2,cs,h3,h2,uv);
    //vec3 w5 = white*square(v2,0.11,-cs,h4,uv);

    vec3 sr1 = red*smoothSquare(-cs,v1*t1,h1,cs,uv,s);
    vec3 sy1 = yellow*smoothSquare(-cs,v1*t3,h3*t3,-0.235*t2,uv,s);
    vec3 sb1 = blue*smoothSquare(0.135*t1,cs,-cs,h4*t2,uv,s);

    vec3 sw1 = white*smoothSquare(-cs,v1*t3,-0.21*t2,h2,uv,s);
    vec3 sw2 = white*smoothSquare(-cs,v1*t3,-cs,h4*t3,uv,s);
    vec3 sw3 = white*smoothSquare(v2*t1,cs,h1,cs,uv,s);
    vec3 sw4 = white*smoothSquare(v2*t3,cs,h3*t2,h2,uv,s);
    vec3 sw5 = white*smoothSquare(v2*t3,0.11*t1,-cs,h4*t2,uv,s);

    //vec3 color = bg+r1+y1+b1+w1+w2+w3+w4+w5;
    vec3 color = bg+sr1+sy1+sb1+sw1+sw2+sw3+sw4+sw5;

    fragColor = vec4(color,1.0);
}
