void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    // Create a circle
    float dist = pow(uv.x * iResolution.x/iResolution.y - 0.5, 2.) + pow(uv.y - 0.5, 2.);

    // Select two colors
    vec3 color1 = vec3(1., .35, 0.);
    vec3 color2 = vec3(1., 0., 0.);


    // Output to screen
    fragColor = vec4(vec3(1. - 10.*dist) * vec3(10.*uv.x) * color1, 1.0) + vec4(vec3(1. - 10.*dist) * vec3(10.*uv.x) * color2, 1.0);
}
