void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord *2.0 - iResolution.xy) / iResolution.y;
    uv = fract(uv*(music(iTime)/8.+1.)+iTime/2.);
    uv.x -= sin(iTime)/2.+0.5;
    uv.y -= cos(iTime)/2.+0.5;
    float d = length(uv*(mod(iTime*2.,1.)*4.+5.));
    d = sin(d);
    d=abs(d);

    fragColor = vec4(d,d,d,1.);
}

// sound in common pass
vec2 mainSound( int samp, float time )
{
    return music(time);
}

float kick(float time, float mag) {
    return sin(mag/(mod(time*2.,1.)+.1))*mod(2.*-time,1.0);
}

float fadeInorOut(float time, float speed) {
     return mod(speed*time,1.);
}
vec2 music(float time)
{
    return vec2( float(int(fract(6.2831*440.0*time*float(int(time*8.)&int(time*4.))/32.)*255.)/128))*fadeInorOut(time,2.)+kick(time,4.);
}
