#define PI 3.1415926535

mat2 rot2D(float a)
{
    float c = cos(a);
    float s = sin(a);
    return mat2(c, -s, s, c);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord * 2. - iResolution.xy) / iResolution.y;

    float tScale = 13.5;
    float t = iTime;
    t = mod(t, tScale);

    float aScale = 8.3;
    float a = clamp(sin(t * PI / tScale) * aScale, 0., 1.);

    vec3 col = vec3(0);

    t += 500.;

    uv *= rot2D(t * .125);

    float theta = atan(uv.y, uv.x);

    float d = length(uv * 3.);
    float _d = d;

    d -= t * .3;
    float dir = sign(mod((d + .5) / 2., 2.) - 1.);
    float scale = clamp(0.38 / _d, 0., .3);
    d -= sin(theta * 7. + t * dir * scale) * .06 * _d;
    d = abs(1. - mod(d - .5, 2.));

    col += pow(d, 4.);

    float c = clamp(_d - .2, 0., 1.);
    col *= mix(vec3(.7, .9, 1.), vec3(0., 0.5, 1.), c * c);

    float v = 1. - (_d - 3.5) * .4;
    col *= clamp(v, 0., 1.);

    fragColor = vec4(col * a, 1.0);
}
