#define BACKGROUND vec3(.1)
#define PARALLAX .05 //positive if crosseyed, negative if parallel
float astep(float edge, float x) {
    return clamp((x-edge)/fwidth(x)+.5,0.,1.);
}
float afloor(float x) {
    return round(x)+astep(round(x),x)-1.;
}
float afract(float x) {
    return x-afloor(x);
}
vec3 texture(vec2 uv) {
    return vec3(afract(5.*((uv.x)*(uv.y)+(iTime*.1))));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord*2./iResolution.y+vec2(-iResolution.x/iResolution.y,-1.); //-1 to 1, no stretching
    float aspect = iResolution.x/iResolution.y; //above 1 if screen is wider than tall
    vec3 col;
    if(uv.x>0.) {
      uv /= min(aspect*.5,1.);
      uv -= vec2(1.,0.);
      uv = mix(uv,vec2(0.),-.2);
      col = texture(uv+vec2(PARALLAX,0.));
    } else {
      uv /= min(aspect*.5,1.);
      uv -= vec2(-1.,0.);
      uv = mix(uv,vec2(0.),-.2);
      col = texture(uv-vec2(PARALLAX,0.));
    }
    col = mix(col,BACKGROUND,astep(1.,uv.x*uv.x+uv.y*uv.y));

    fragColor = vec4(col,1.0);
}
