/*original https://www.shadertoy.com/view/lllyRn  https://www.shadertoy.com/view/DlycWR https://www.shadertoy.com/view/4tyfWy*/


#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define H(h)(cos((h)*2.3+vec3(2,32,11))*.5+.5)
float funny_star(vec2 uv, float anim)
{
    uv = abs(uv);
    vec2 pos = min(uv.xy/uv.yx, anim);
    float p = (2.0 - pos.x - pos.y);
    return (2.0+p*(p*p-1.5)) / (uv.x+uv.y);      
}
vec3 pulsar(in vec3 p, in vec2 uv)
{
	float	str = 15.0,
        	accum = 0.5,
        	prev = mod((iTime+sin(iTime*20.0)*0.025)*10.1, 60.0),
        	tw = 1.0;

    p.z += sin(iTime*1.0)*0.01;

	for (int i = 0; i < 15; ++i)
    {
		float mag = dot(p, p);
		p = abs(p) / mag + vec3(-.5, -.42 - cos(iTime*0.2)*0.02, -1.75 + sin(iTime*0.2)*0.003);
		float w = exp(-float(i) / str);
		accum += w * exp(-str * pow(abs(mag - prev), 2.2));
		tw += w;
		prev = mag;
	}
    float t = clamp(5.0 * accum / tw - 0.9, 0.0, 1.0);
	return vec3(1.3*t*t*t*(1.0 - length(uv)*0.2) , 1.2*t*t, 1.0*t);
}
mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

float nice_star(vec2 uv, float anim)
{
    uv = abs(uv);
    vec2 pos = min(uv.xy/uv.yx, anim);
    float p = (2.0 - pos.x - pos.y);
    return (2.0+p*(p*p-1.5)) / (uv.x+uv.y);      
}

void mainImage(out vec4 O, vec2 C)
{


vec2 offset = iResolution.xy * 0.5 + (iMouse.z > 0.5 ? iMouse.xy : vec2(0.0));
    vec2 uv = 2.0 *  C.xy / iResolution.xy - 1. + offset/iResolution.xy - 0.5;
	vec3 p2 = vec3(uv * (iResolution.xy / iResolution.x) / 0.2, 0.0);

    vec3 noise = texture(iChannel0, uv*(3.0-mod(iTime*2.0,0.2))).rgb*0.1;

    vec2 vig_coord =  C.xy / iResolution.xy - pow( C.xy / iResolution.xy,vec2(2.0));
    float vig_highlight = 1.0-pow(vig_coord.x*vig_coord.y*3.0, 0.05);
  p2.xy= rotate2d( sin(iTime/5.)*3.14 ) * p2.xy;

 uv *= 2.0 * ( cos(iTime * 2.0) -2.5);

    // anim between 0.9 - 1.1
    float anim = sin(iTime * 12.0) * 0.1 + 1.0;    


    O=vec4(0);
    	vec4 O2= vec4((vec3(pulsar(p2*0.99,uv).r, pulsar(p2, uv).gb)-noise)+vig_highlight, 1.0) ;
    vec3 p,q,r=iResolution,
    d=normalize(vec3((C*2.-r.xy)/r.y,1));  
    for(float i=0.,a,s,e,g=0.;
        ++i<110.;
        O.xyz+=mix(vec3(1),H(g*.1),sin(.8))*1./e/8e3
    )
    {
        p=g*d;



        p.z+=-iTime*0.0;
        a=10.;
        p=mod(p-a,a*2.)-a;
        s=6.;
        for(int i=0;i++<8;){
            p=.3-abs(p);

            p.x<p.z?p=p.zyx:p;
            p.z<p.y?p=p.xzy:p;

            s*=e=1.4+sin(iTime*.234)*.1;
            p=abs(p)*e-
                vec3(
                    5.+sin(iTime*.3+.5*tan(iTime*.3))*3.,
                    120,
                    8.+cos(iTime*.2)*5.
                 )*O2.xyz;
         }
         g+=e=length(p.yz)/s;

    }
    O*= vec4(funny_star(uv,anim) * vec3(0.55,0.5,0.55), 1.0);
}
