void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    float sound = texture(iChannel0, vec2(0.5, 0.0)).r;

    vec3 col = 0.5 + 0.5 * cos(sound * + uv.xyx + vec3(0, 1, 1));

    float radius = 0.1 + 0.5 * sound; 

    vec2 center = vec2(0.5, 0.5); 

    float dist = length(uv - center);

    float sinColorValue = sin(uv.x * 3.0 + sound * 10.0);
    vec3 circleColor = vec3(0.5 + 0.5 * sinColorValue);
    if (dist < radius) {
        fragColor = vec4(vec3(fract(circleColor*10.0)),1.0);
    } else {
        fragColor = vec4(col, 1.0); 
    }

}
