void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 p = ivec2(fragCoord);
    vec2 uv = fragCoord/iResolution.xy;
    int blurx = int(11.+10.*sin(iTime));
    int blury = blurx;
    vec4 a = texelFetch(iChannel0, p-ivec2(blurx,0), 0);
    vec4 b = texelFetch(iChannel0, p-ivec2(0,0), 0);
    vec4 c = texelFetch(iChannel0, p-ivec2(blurx,blury), 0);
    vec4 d = texelFetch(iChannel0, p-ivec2(0,blury), 0);
    vec4 col = b-a-d+c;
    col /= col.a;
    fragColor = vec4(col.rgb, 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 p = ivec2(fragCoord);
    vec2 uv = fragCoord/iResolution.xy;
    vec4 a = texelFetch(iChannel0, p-ivec2(1,0), 0);
    vec4 b = texture(iChannel1, uv);
    vec4 c = texelFetch(iChannel0, p-ivec2(1,1), 0);
    vec4 d = texelFetch(iChannel0, p-ivec2(0,1), 0);
    vec4 col = a+d-c+b;
    fragColor = col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = texture(iChannel0, uv).rgb;

    // Output to screen
    fragColor = vec4(col,1.0);
}
