float plot(vec2 st, float pct){
  return  smoothstep( pct-0.02, pct, st.y) -
          smoothstep( pct, pct+0.02, st.y);
}

float smoothINV_ACES(float x)
{
    float DELTA = 0.04;// Modify this value if you want to change the way the functions are mixed together
    float MERGE_POINT = 0.0225;
    float INV_ACES = (sqrt(-10127.*x*x + 13702.*x+9.) + 59.0*x-3.0) / (502.0 - 486.0*x);
    return mix(x, INV_ACES, 1.0-smoothstep(MERGE_POINT-DELTA, MERGE_POINT+DELTA, x));

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 st = gl_FragCoord.xy/iResolution.xy;

    float y = smoothINV_ACES(st.x);
    //float y = (sqrt(-10127.*x*x + 13702.*x+9.) + 59.0*x-3.0) / (502.0 - 486.0*x); // For comparison

    vec3 color = vec3(y);
    float pct = plot(st,y);
    color = (1.0-pct)*color+pct*vec3(0.0,1.0,0.0);

    fragColor = vec4(color,1.0);
}
