#define R 5.

float leftSide(float x, float y) {
    return pow(x*x+y*y-1.,3.);
}

float rightSide(float x, float y) {
    return x*x*y*y*y;
}

/*float*/bool equation(vec2 fragCoord) {
    vec2 uv = 2.*(fragCoord*2.-iResolution.xy)/iResolution.y;
    /*float ret = abs(leftSide(uv.x, uv.y)-rightSide(uv.x,uv.y));
    if (ret<.01)
        return ret;
    return 1000.;*/
    return leftSide(uv.x,uv.y)>rightSide(uv.x,uv.y);
}

bool checkPix(bool pix0, vec2 pos) {
    return pix0^^equation(pos);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = 2.*(fragCoord*2.-iResolution.xy)/iResolution.y;
    if (abs(uv.y)<.01) fragColor=vec4(1,0,0,1);
    else if (abs(uv.x)<.01) fragColor=vec4(0,1,0,1);
    else if (abs(fract(uv.x-.5)-.5)<.01||abs(fract(uv.y-.5)-.5)<.01) fragColor = vec4(.2);
    else fragColor = vec4(0);
    /*float pix0 = equation(fragCoord);
    if (pix0<1000.) {
        float pix1 = equation(fragCoord + vec2(1,0));
        float pix2 = equation(fragCoord + vec2(0,1));
        float pix3 = equation(fragCoord - vec2(1,0));
        float pix4 = equation(fragCoord - vec2(0,1));
        if (min(min(min(min(pix0,pix1),pix2),pix3),pix4) == pix0)
            fragColor = vec4(1);
    }*/
    bool pix0 = equation(fragCoord);
    if(checkPix(pix0,fragCoord+vec2(R,0))||
       checkPix(pix0,fragCoord+vec2(0,R))||
       checkPix(pix0,fragCoord-vec2(R,0))||
       checkPix(pix0,fragCoord-vec2(0,R))||
       checkPix(pix0,fragCoord+vec2(R*sqrt(.5)))||
       checkPix(pix0,fragCoord-vec2(R*sqrt(.5)))||
       checkPix(pix0,fragCoord+R*sqrt(.5)*vec2(-1,1))||
       checkPix(pix0,fragCoord+R*sqrt(.5)*vec2(-1,1)))
        fragColor = vec4(1);
}
