bool isWhite(in vec3 col)
{
    return (col[0]+col[1]+col[2] < 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 q = fragCoord.xy / iResolution.xy;
    vec3 col = texture(iChannel0, q).rgb;
    vec2 nearest = vec2(0, 0);
    float nearestDist = 9999.;
    for (float y = 0.5; float(y) < iResolution.y; y++) {
        for (float x = 0.5; float(x) < iResolution.x; x++) {
            vec2 location = vec2(x, y);
            vec3 pixel = texture(iChannel0, location/iResolution.xy).rgb;
            float dist = distance(location, fragCoord);
            if (isWhite(pixel) && dist < nearestDist) {
                nearestDist = dist;
                nearest = location;
            }
        }
    }



    vec2 diff = normalize(fragCoord-nearest);
    vec3 res = vec3(0,0,0);
    res = vec3(0., (1.+diff.x*-1.)/2., (1.+diff.y*-1.)/2.);

    //uncomment if you wanna see the black and white "source" image
    /*
    if (isWhite(col)) {
        res = vec3(1., 1., 1);
    } else {
        res = vec3(0., 0., 0);
    }
    //*/

	fragColor = vec4(res, 1.0); 
}

