void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec2 dist;
    // 4 samples of rock tex
    for(int i = 1; i < 32; i++){
    vec4 samp = texture(iChannel2,vec2(float(i),0.0) / iChannelResolution[2].xy) - 0.5;
    vec4 samp2 = texture(iChannel2,vec2(float(i),1.0) / iChannelResolution[2].xy) - 0.5;
    dist += texture(iChannel1,(uv*((float(i)*0.05)+0.95))+vec2((iTime + samp.g) * samp2.r,(iTime + samp.r) * samp2.g)).rr * samp.ba;
    }
    // final color
    vec3 col = texture(iChannel0,uv - dist * 0.02).rgb;
    col += pow(dot(normalize(vec3(dist.x,1.0,dist.y)),vec3(0.0,1.0,0.0)),64.0);
    fragColor = vec4(col,1.0);
}
