#define pi 3.14159

//https://iquilezles.org/articles/palettes/
vec3 palette( float t ) {
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.263,0.416,0.557);

    return a + b*cos( 6.28318*(c*t+d) );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec3 uv = vec3((fragCoord * 2.0 - iResolution.xy) / iResolution.y, 0.);
    uv *= 8.;
    vec3 finalColor = vec3(0.0);

    vec3 a = vec3(cos(2.*pi/6.) * 1., sin(2.*pi/6.) * 1., sin(iTime * .256));
    vec3 b = vec3(cos(4.*pi/6.) * 1., sin(4.*pi/6.) * 1., sin(iTime *  .02));
    vec3 c = vec3(cos(6.*pi/6.) * 1., sin(6.*pi/6.) * 1., sin(iTime * .241));
    vec3 d = vec3(cos(8.*pi/6.) * 1., sin(8.*pi/6.) * 1., sin(iTime * .13));
    vec3 e = vec3(cos(10.*pi/6.) * 1., sin(10.*pi/6.) * 1., sin(iTime * -.111));
    vec3 f = vec3(cos(12.*pi/6.) * 1., sin(12.*pi/6.) * 1., sin(iTime * .113));

    float time = 20.*iTime;

    finalColor += vec3(pow(sin(length(uv - a) * 50. - time) + 1., 1.) / 3., pow(sin(length(uv - a) * 50. - time) + 1., 1.) / 3., (sin(length(uv - a) * 50. - time) + 2.) / 1.5);
    finalColor += vec3(pow(sin(length(uv - b) * 50. - time) + 1., 1.) / 3., pow(sin(length(uv - b) * 50. - time) + 1., 1.) / 3., (sin(length(uv - b) * 50. - time) + 2.) / 1.5);
    finalColor += vec3(pow(sin(length(uv - c) * 50. - time) + 1., 1.) / 3., pow(sin(length(uv - c) * 50. - time) + 1., 1.) / 3., (sin(length(uv - c) * 50. - time) + 2.) / 1.5);
    finalColor += vec3(pow(sin(length(uv - d) * 50. - time) + 1., 1.) / 3., pow(sin(length(uv - d) * 50. - time) + 1., 1.) / 3., (sin(length(uv - d) * 50. - time) + 2.) / 1.5);
    finalColor += vec3(pow(sin(length(uv - e) * 50. - time) + 1., 1.) / 3., pow(sin(length(uv - e) * 50. - time) + 1., 1.) / 3., (sin(length(uv - e) * 50. - time) + 2.) / 1.5);
    finalColor += vec3(pow(sin(length(uv - f) * 50. - time) + 1., 1.) / 3., pow(sin(length(uv - f) * 50. - time) + 1., 1.) / 3., (sin(length(uv - f) * 50. - time) + 2.) / 1.5);

    finalColor = vec3(palette(finalColor.x).x, palette(finalColor.y).y * .17, palette(finalColor.z).z * .2);


    fragColor = vec4(finalColor, 1.0);
}
