vec3 clamp_line( in vec3 col, 
                in float center, 
                in float p, 
                in float line_width,
                in vec3 line_col) {
    if ((p > center - line_width/2.0) && (p < center + line_width/2.0)) {
        return line_col;
    } else {
        return col;
    }

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float r0, r1 = 0.0;
    vec3 col = vec3(0.0, 0.0, 0.0);

    // Normalized pixel coordinates (from 0 to 1)
    //vec2 uv = fragCoord/iResolution.xy;
    vec2 uv = ( fragCoord -.5*iResolution.xy )  /iResolution.y;
    uv += vec2(0.5);

    // background colour (1st color):
    r0 = abs(cos(iTime*1.0));
    vec3 color_0 = vec3(r0, abs(cos(iTime))*r0, 0);
    col = color_0;

    // 2nd color (opposite of first):
    r1 = abs(sin(iTime*1.0));
    vec3 color_1 = vec3(r1, abs(sin(iTime))*r1, 0);

    float line_width = 0.5*abs(cos(iTime*0.5));

    col = clamp_line(col, 0.75, uv.y, line_width, color_1);
    col = clamp_line(col, 0.75+0.15/2.0, uv.y, line_width/8.0, color_0);
    col = clamp_line(col, 0.75-0.15/2.0, uv.y, line_width/8.0, color_0);

    col = clamp_line(col, 0.25, uv.y, line_width, color_1); 
    col = clamp_line(col, 0.25+0.15/2.0, uv.y, line_width/8.0, color_0);
    col = clamp_line(col, 0.25-0.15/2.0, uv.y, line_width/8.0, color_0);

    col = clamp_line(col, 0.75, uv.x, line_width, color_1);
    col = clamp_line(col, 0.75+0.15/2.0, uv.x, line_width/8.0, color_0);
    col = clamp_line(col, 0.75-0.15/2.0, uv.x, line_width/8.0, color_0);

    col = clamp_line(col, 0.25, uv.x, line_width, color_1);
    col = clamp_line(col, 0.25+0.15/2.0, uv.x, line_width/8.0, color_0);
    col = clamp_line(col, 0.25-0.15/2.0, uv.x, line_width/8.0, color_0);

    // Output to screen
    fragColor = vec4(col,1.0);
}
