#define R3D(a) mix(q*dot(p,q), p, cos(a)) + sin(a)*cross(p,q)

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e,t=iTime; vec3 p,q=vec3(-2),r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,e);
    p.z-=2.;
    p=p*R3D(t);

    for(j=0.;j++<4.;)
        p=abs(abs(p*e-.1)-.04)-.02,
        d=max(min(p.x,p.z),p.y),
        d-=dot(p,q)/1e4;

    e+=d;

}o+=vec4(exp(p/e),1)*8./i; 

}
