float field(vec2 uv) {
    return (
        (cos(sin(uv.y * 10.3) + uv.x * 6.7) / 2.0 + 0.5) +
        (sin(cos(uv.y * 7.3 + 20.64  ) + uv.x * 13.7 - 24.33) / 2.0 + 0.5)
    ) / 2.0;
}

vec3 palette(float col) {
    vec3 c1 = vec3(0.8, 0.1, 1.0);
    vec3 c2 = vec3(0.3, 1.0, 0.3);

    vec3 c = mix(c2, c1, sin(iTime * 0.1 + col * 30.0) / 2.0 + 0.5);

    return c * ((-col) * 4.0 * (col - 1.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord / iResolution.xy * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    float f0 = field(uv);
    float f1 = field(uv);

    float scroll = iTime * 0.1;
    vec2 scale = vec2(uv.x * 0.8, uv.y * 2.0);
    float offset = iTime * -0.04;

    float col1 = field(scale + vec2(scroll, 0.0));
    float col2 = field(scale + vec2(-scroll + offset, offset));

    float soup = col1 + col2;
    soup = min(soup, step(soup, 1.3)) * 0.8;



    fragColor = vec4(palette(soup), 1.0);
}
