#define PI 3.1415926538

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float freq;
    float zoomSinSpeed = 50.0;
    float diagonalShakeSpeed = 57.0;
    float zoomSinSpace = 0.35;
    float diagonalShakeSpace = 0.35;

    float overallEditSpace = 300.;
    float agressionControl = 0.0;

    freq = texture(iChannel1, vec2(.01,.25)).r - agressionControl;


    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 newUV = 0.5 + (uv.xy+tan(freq*atan(overallEditSpace))) * vec2((uv * - zoomSinSpace * sin(iTime * zoomSinSpeed) + 0.) + diagonalShakeSpace * cos(iTime * diagonalShakeSpeed) + 0.);

    vec2 displacement1 = newUV;
    float progress = 1000.0 *  (.55+.2*freq) / iResolution.x -1.0;


    displacement1.xy *= atan(progress*PI/42.)/1.;

    vec3 channelEdit;

    channelEdit.r = texture(iChannel0,vec2(displacement1+uv +0.0025)).x;
    channelEdit.g = texture(iChannel0,vec2(displacement1+uv +0.001)).y;
    channelEdit.b = texture(iChannel0,vec2(displacement1+uv +0.001)).z;

    channelEdit = clamp(channelEdit*0.02+1.25*channelEdit*channelEdit*1.6,0.0,1.0);

    // Time varying pixel color
    vec4 tex = texture(iChannel0, displacement1+uv);

    tex *= 0.9+0.1*sin(15.0*iTime+uv.y*1000.0);
    channelEdit += vec3(tex)*pow(freq,5.);


    // Output to screen
    fragColor = vec4(channelEdit, 1.0);


}
