float _pixSize ;
// default colors 
const vec3 _co = vec3( .9,0.3,0.0); // orange 
const vec3 _cb = vec3(0.0,0.36,1.0); // blue


float offsetByIdx(float idx)
{
    return (iResolution.y*(idx*1.0/3.0 + 1.0/6.0)); 
}
bool isRange(vec2 fragCoord, float idx)
{
   return fragCoord.y > iResolution.y*(idx*1.0/3.0) &&
          fragCoord.y < iResolution.y*((idx+1.0)*1.0/3.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float _1d  = texelFetch(iChannel0,ivec2(0,0),0).x;

    if (_1d !=0.0)
    {
        vec2 c = vec2(iResolution.x/2.0,0.0);
        // sample range with idx = 2
        // Jacobi method
        // blue
        vec2 pCoord = vec2( fragCoord.x, offsetByIdx(2.0));
        float H = texelFetch(iChannel0,ivec2(pCoord),0).y;
        float ar = iResolution.x/iResolution.y;
        vec2 vx = (fragCoord/iResolution.y)*vec2(2.0,2.0)-vec2(ar,0.0);
        float fh =smoothstep(0.0, 0.01, abs(vx.y-H));

        // sample range with idx = 1 
        // WOS method
        pCoord = vec2( fragCoord.x, offsetByIdx(1.0));
        vec4 v = texelFetch(iChannel0,ivec2(pCoord),0);
        H = (v.y/(v.w));
        float fhwos =smoothstep(0.0, 0.01, abs(vx.y-H));

        // sample range with idx = 0 
        // green function convolution
        pCoord = vec2( fragCoord.x, offsetByIdx(0.0));
        v = texelFetch(iChannel0,ivec2(pCoord),0);
        float fhgreen =smoothstep(0.0, 0.01, abs(vx.y-v.y));

        // exact solution 
        // black
        float fx =( 2.0-2.0*vx.x*vx.x); //[0,2]
        float dxx = abs(fx - vx.y);
        float fxx = smoothstep(0.0,.01, dxx);

        //
        vec3 bk = vec3(.9);
        vec3 O = mix(bk, vec3(0.0,0.0,0.0), 1.0-fhwos);
        O = mix(O, vec3(1.0,0.0,0.0), 1.0-fhgreen);
        O = mix(O, vec3(0.0,0.0,1.0), 1.0-fh);
        //O =O* fxx;
        fragColor =  vec4(O,1.0);
    }
   else
   {
        vec4 O = texelFetch(iChannel0,ivec2(fragCoord),0);
        float l0 = iResolution.y/3.0;
        O = vec4( mix( _co*2.0,O.xyz, smoothstep(0.0,1.0,abs(l0-fragCoord.y))),O.w);
        l0 *= 2.0;
        O = vec4( mix( _co*2.0,O.xyz, smoothstep(0.0,1.0,abs(l0-fragCoord.y))),O.w);
        fragColor = isRange(fragCoord,1.0) ? vec4(O.xyz/(2.0*O.w),1.0) : O/2.0;

    }

}

//
// solve laplacian(u(x,y))=-4  with zero boundary condition on [0,1]
// then compare to analytic solution y = 2-2x^2 on [-1,1] 


// 1 -> raw green function
// 2 -> simplified geen function log(1/x) = -log(x)
// 3 -> use analytic integration of the distribution factorizing the constant "source term".
#define METHOD 3 


#define ToCol(x,y,z) (vec3((x+1.0)/2.0, (y+1.0)/2.0,(z+1.0)/2.0))
#define ToN(v3) (vec3((v3.x*2.0)-1.0, (v3.y*2.0)-1.0,(v3.z*2.0)-1.0))


ivec2 offset(ivec2 p, ivec2 o, ivec2 bounds)
{
    return  ivec2(  o.x, 
                  p.y+o.y < bounds.y ? 0 : o.y);
} 

