// oldschool rand() from Visual Studio
// --------------------------------------
int  seed = 1;

int randi(void) {
    seed = seed * 0x343fd + 0x269ec3;
    return (seed >> 16) & 32767;
}
float rand(void) {
    return float(randi())/32767.0;
}
// --------------------------------------

// --------------------------------------
// hash to initialize the random sequence (copied from Hugo Elias)
// --------------------------------------
int hash( int n ) {
	n = (n << 13) ^ n;
    return n * (n * n * 15731 + 789221) + 1376312589;
}

// --------------------------------------

vec2 randomOnCircle(  ) {
    float theta = 6.28318530 * rand();
    return vec2(cos(theta), sin(theta));
}

// https://dl.acm.org/doi/pdf/10.1145/3386569.3392374
// harmonic Greenâ€™s function of the Laplace operator on ðµ, in 2D
// straight implementation is producing a non converging result.
float G(float R) {
    float r = R*(sqrt(rand()+0.0001));
    return log(R/r)/6.2831853071795;
}
// rewrite green function with ln(1/x)=-ln(x)  
float G1()
{
    return -log(sqrt(rand()+0.001))/6.28318530;
}

// WoS
vec3 march( in vec2 p )
{
    vec4 h=vec4(0.0);
    float u=0.0; //solution estimate
    if (p.x<-1.0 || p.x >1.0) return vec3(0.0);
	for( int i=0; i<32; i++ )
    {
        h.x = min(abs(p.x+1.0), abs(p.x-1.0)); // Distance to boundary. 
                                       // The function we are after is zero on boundary.  

       #if (METHOD == 1)
           u-= 3.1415926535*h.x*h.x*G(h.x)*-4.0;
       #elif (METHOD ==2)
           u-= 3.1415926535*h.x*h.x*G1()*-4.0;
       #else
           u+= h.x*h.x; // use the green function probalbility distribution R^2/4
        #endif
        if( h.x<0.001 ) break;
        p = p + h.x*randomOnCircle();
    }
    return vec3(u);
}
// 1d green function with dirichlet bounary condition 0 on [0,2]
float G1D(float x, float y)
{

    return  x>=y ?y*(1.0-x/2.0)*2.0
                 : (1.0-y/2.0)*x*2.0;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //set random seed 
    ivec2 q = ivec2(fragCoord);
    seed = hash(q.x + hash(q.y + hash(iFrame)));
    //
    vec2 uv = fragCoord/iResolution.y;
    float pixSize = 1.0/iResolution.y;

    vec3 col = vec3(0.0);
    float c = iResolution.y/2.0;
    if (fragCoord.x<1.0 && fragCoord.y<1.0)
    {
        // save mouse state.
        vec4 mouseState =texelFetch(iChannel0,ivec2(0,0),0);
        mouseState.x = iMouse.w>0.0 ? mouseState.x ==0.0 ? 1.0 : 0.0 :mouseState.x;
        mouseState.yz = iMouse.xy;
        fragColor = mouseState;
    }
    else
    {
          float ar = iResolution.x/iResolution.y;
          vec2 v = (fragCoord/iResolution.y)*vec2(2.0,2.0)-vec2(ar,0.0);

          int method =  int((fragCoord.y/iResolution.y)*3.0);
          ivec2 bounds;
          switch(method)
          {
              case 0:
                // 1D green function convolution.
                if ( v.x>=-1.001  && v.x <= 0.999)
                {
                    float u=0.0;
                    float stepi= ar*2.0/iResolution.y;
                    for (float i=-1.0; i<1.0; i+=stepi)
                    {
                      u+=4.0*G1D(v.x+1.0,i+1.0); 
                    }
                    u/=2.0/stepi;
                    fragColor = vec4(vec3(u),1.0);
                }
                else 
                fragColor = vec4(vec3(0.0),1.0);
                break;
              case 1:


                fragColor =texelFetch(iChannel0,ivec2(fragCoord),0);
                fragColor += vec4(march(v),1.0);

                break;
              case 2 :

                if ( v.x>=-1.001  && v.x <= 0.999)
                {
                    for(int i=0;i<512+ min(0,iFrame);++i)
                    {
                        bounds =   ivec2(int((iResolution.y+1.0)*2.0/3.0),int(iResolution.y));
                        // Laplace diffusion.
                        // jacobi method // slow convergence
                        float h = 2.0*ar/iResolution.x;
                        col=texelFetch(iChannel0,ivec2(fragCoord)+ offset(ivec2(fragCoord),ivec2(-1,0),bounds),0).xyz;
                        col+=texelFetch(iChannel0,ivec2(fragCoord)+offset(ivec2(fragCoord),ivec2(1,0),bounds),0).xyz;
                        col+=texelFetch(iChannel0,ivec2(fragCoord)+ offset(ivec2(fragCoord),ivec2(0,1),bounds),0).xyz;
                        col+=texelFetch(iChannel0,ivec2(fragCoord)+ offset(ivec2(fragCoord),ivec2(0,-1),bounds),0).xyz;
                        col -=h*h*-4.0;
                        col*=.25;
                    }
                 }
                 else
                 {
                     col = vec3(0.0);
                 }
                 fragColor = vec4(col,255);
                 break;
           }
    }

}
