vec3 palette(float t) {

    vec3 a = vec3(0.8, 0.2, 0.2);

    vec3 b = vec3(0.2, 0.5, 0.8);

    vec3 c = vec3(0.2, 0.8, 0.2);

    vec3 d = vec3(0.8, 0.8, 0.2);

    return a + b * cos(6.28318 * (c * t + d));

}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv=fragCoord/iResolution.xy*2.-1.;
    vec2 uv0=uv;
    uv=fract(uv)-0.5;
    float d=abs(length(uv)-0.5*sin(iTime*4.));
    d=0.1/d;
    float d1=sin(length(uv0)*iTime);
    d=pow(d,1.3);
    vec3 color=vec3 (d)*palette(iTime*d1)*vec3 (d1,d1,d1);
    fragColor =vec4 (color,1.0);
}
