vec3 pal(float t){
    vec3 a = vec3(0.957, 0.415, 0.641);
    vec3 b = vec3(0.692, 0.515, 0.475);
    vec3 c = vec3(0.683, 1.171, 1.005);
    vec3 d = vec3(2.168, 2.388, 2.498);

    return a + b*cos(6.8318*(c*t+d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord/iResolution.xy)*2. -1.;
    uv.x *= (iResolution.x/iResolution.y);
    vec3 col = -2.*(vec3(length(uv)) -1.);
    col*= pal(iTime);
    col *= 1./length(uv);
    col = log(col);
    fragColor = vec4(col,1.0);
}
