const float pi = 3.1415926;
void mainImage( out vec4 O, vec2 U ){
    vec2 r = iResolution.xy;
    vec2 uv = (2.*U-r)/r.y;

    float t = atan(uv.x, uv.y) - pi;
    O = vec4(cos(t + vec4(0., 2.*pi/3., 4.*pi/3., 0.))*.5+.5) * length(uv) * (-cos(iTime)  + 2.);
