void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3  c1 = vec3(0.3);
    vec3  c2 = texelFetch(iChannel0, ivec2(fragCoord), 0).rgb;

    vec2  p  = (fragCoord * 2.0 - iResolution.xy);
    float d  = length(p) - iResolution.y * 0.8;
    float f  = smoothstep(2.0, -2.0, d);

    fragColor = vec4(mix(c1, c2, f), 1.0);   
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(vec3(0.8),1.0);
}
