// Simplex Noise (http://en.wikipedia.org/wiki/Simplex_noise), a type of gradient noise
// that uses N+1 vertices for random gradient interpolation instead of 2^N as in regular
// latice based Gradient Noise.

// All noise functions here:
//
// https://www.shadertoy.com/playlist/fXlXzf&from=0&num=12



vec2 hash( vec2 p ) // replace this by something better
{
	p = vec2( dot(p,vec2(127.1,311.7)), dot(p,vec2(269.5,183.3)) );
	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}

float noise_( in vec2 p )
{
    const float K1 = 0.366025404; // (sqrt(3)-1)/2;
    const float K2 = 0.211324865; // (3-sqrt(3))/6;

	vec2  i = floor( p + (p.x+p.y)*K1 );
    vec2  a = p - i + (i.x+i.y)*K2;
    float m = step(a.y,a.x); 
    vec2  o = vec2(m,1.0-m);
    vec2  b = a - o + K2;
	vec2  c = a - 1.0 + 2.0*K2;
    vec3  h = max( 0.5-vec3(dot(a,a), dot(b,b), dot(c,c) ), 0.0 );
	vec3  n = h*h*h*h*vec3( dot(a,hash(i+0.0)), dot(b,hash(i+o)), dot(c,hash(i+1.0)));
    return dot( n, vec3(70.0) );
}

float noise(vec2 fragCoord) {
    vec2 p = fragCoord.xy / iResolution.xy;

	vec2 uv = p*vec2(iResolution.x/iResolution.y,1.0) + iTime*0.25;

	float f = 0.0;
	uv *= 5.0;
    mat2 m = mat2( 1.6,  1.2, -1.2,  1.6 );
	f  = 0.5000*noise_( uv ); uv = m*uv;
	f += 0.2500*noise_( uv ); uv = m*uv;
	f += 0.1250*noise_( uv ); uv = m*uv;
	f += 0.0625*noise_( uv ); uv = m*uv;
    f = 0.5 + 0.5*f;
    return f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const int pw = 7, ph = 5;
    float aspect = iResolution.x / iResolution.y;
    vec2 points[pw * ph];
    float weights[pw * ph];
    vec2 p = fragCoord.xy / iResolution.xy;
    vec2 uv = p*vec2(iResolution.x/iResolution.y,1.0);

    for(int w = 0; w < pw - 2; w++) {
        for(int h = 0; h < ph - 2; h++) {
            vec2 i = vec2(
                aspect * float(w + 1) / float(pw - 1),
                float(h + 1) / float(ph - 1)
            );
            points[ph * w + h] = i;
        }
    }
    vec2 closest = points[0];
    for(int i = 0; i < pw * ph; i++) {
        if (distance(closest, uv) < distance(points[i], uv) && points[i] != vec2(0., 0.))
            closest = points[i];
    }
    float f = noise(fragCoord);
    f -= distance(uv, closest) * .4;
    f -= distance(p, vec2(.5, .5)) * 0.35;
    vec4 res;
    vec4 base = vec4(f, f, f, 1.0 );
    if(f < .0)
        res = vec4(0.098, 0.380, 0.545, 1.) + base * 0.2;
    else if (f < .07)
        res = vec4(0.909, 0.921, 0.643, 1.) - base * 1.2;
    else if (f < .25)
        res = vec4(0.471, 0.686, 0.435, 1.) - base * 0.7;
    else if (f < .31)
        res = vec4(f*f, f*f, f*f, 1.) * 5.;
    else
        res = vec4(f*f, f*f, f*f, 1.) * 7.5;
	fragColor = res;
}
