// Shadow Adjustment Shader

const float SHADOWS = 0.0;
const vec3 SHADOWS_RGB = vec3(1.0, 1.0, 1.0);

vec3 adjustShadows(vec3 color, float shadowAdjust, vec3 shadowAdjustRGB) {
    float luminance = dot(color, vec3(0.299, 0.587, 0.114));
    float shadowFactor = smoothstep(0.0, 0.3, luminance);
    vec3 adjustedColor = color * (1.0 - shadowAdjust);
    adjustedColor *= shadowAdjustRGB;
    return mix(color, adjustedColor, 1.0 - shadowFactor);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;

    vec3 color = texture(iChannel0, uv).rgb;

    color = adjustShadows(color, SHADOWS, SHADOWS_RGB);

    color = clamp(color, 0.0, 1.0);

    fragColor = vec4(color, 1.0);
}
