#define r(a) mat2(cos(a + vec4(0, 33, 11, 0)))

void mainImage(out vec4 O, vec2 u) {
    vec3  p = iResolution; 
          u = (u + u - p.xy) / p.y;  

    float c, i, d, d1, d2, a, T = iTime * .5;

    O *= 0.;
    while(i++ < 120.){
        p = .7 * vec3(u * a, a - .03);

        p.yx *= r(T*.73);
        p.xz *= r(T); 

        c = dot(p, p);

        p *= .09 / c;

        p.xy *= r(p.z * .04 * cos(T*11.) );

        p = cos(p);

        d1 = min(length(p.xz), length(p.yz)) * 2.;
        d2 = (5. + cos(T*3.)) * dot(p, p) - (2.4 + cos(T * dot(p.xz,p.xz) * .1));

        d = c * min(d1, d2);

        a += d;

        O += .009 / exp(18000. * d - .1);
        if(d1 < d2)
            O.rg *= .2;
    }
}
