float f(float a, float b, float x, float y) {
   return pow(abs(sin(0.2+pow(a-x*x,2.0)+b*pow(x-y*y,2.0))),0.3);
 }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.x=(uv.x-0.5)*4.0;
    uv.y=(uv.y-0.5)*4.0;

    vec3 col;
    for(float i=0.0;i<3.0;++i) {
        col[int(i)] = f(sin(iTime)*2.0+i*0.1, cos(iTime)*2.2-i*0.1, uv.x, uv.y)*(0.6+0.2*i);
    }
    fragColor = vec4(col,1.0);
}
