void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    float dist = length(uv);
    float ring = sin(dist * 20.0 - iTime * 2.0);
    float angle = atan(uv.y, uv.x);
    float dots = sin(angle * 20.0) * 0.5 + 0.5;
    float pattern = ring * dots;
    float glow = 0.1 / (dist * dist);
    float brightness = pattern * 0.5 + glow;
    vec3 color = vec3(1.0, 0.9, 0.7) * brightness;
    fragColor = vec4(color, 1.0);
}
