void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // resolucao da imagem
    vec2 resolution = iResolution.xy;

    // normalizando as coordenadas de 0 a 1.
    vec2 uv = fragCoord.xy / resolution.xy;

    // Carregando a textura da imagem original
    vec4 originalColor = texture(iChannel0, uv);

    // Convertendo para o efeito sépia.
    vec3 sepiaColor;

    sepiaColor.r = dot(originalColor.rgb, vec3(0.393, 0.769, 0.189));

    sepiaColor.g = dot(originalColor.rgb, vec3(0.349, 0.686, 0.168));

    sepiaColor.b = dot(originalColor.rgb, vec3(0.272, 0.534, 0.131));

    // Aplicando o efeito de sépia
    fragColor = vec4(sepiaColor, originalColor.a);
}
