void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 resolution = iResolution.xy;
    float time = iTime;
    vec2 orangemovement = vec2(0.3 + 0.4*cos(time + 0.5), .0 + 0.4*sin(time + 0.7));
    vec4 orange = vec4(0.8, 0.2, 0.0, 0.);
    vec4 background = texture(iChannel1, fragCoord/ iResolution.xy);
    vec2 watermelon = vec2(0.5 + 0.1*cos(time), .0 + 0.5*sin(time));
    vec4 melon = vec4(0., 0.8, 0.1, 0.);
    vec2 offset = vec2(0.5, 0.0 + sin(time + 0.5));
    vec4 bomb = vec4(0., 0., 0., 0.);
    float distance1 = length(fragCoord - resolution * watermelon);
    float distance2 = length(fragCoord - resolution * offset);
    float distance3 = length(fragCoord - resolution * orangemovement);
    float circleRadius = .1 * resolution.x;
    if (distance1 < circleRadius) {
        background = melon;
    }

    if (distance2 < circleRadius) {
        background = bomb;
    }
    if (distance3 < circleRadius) {
        background = orange;
    }
    fragColor = vec4(background); 
}
