void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

   float time = iTime;

    float stripeWidth = 0.2;
    float diagonal = uv.x + uv.y + (time * 0.5); 
    float stripe = mod(diagonal, stripeWidth * 3.0) - stripeWidth;
    float stripePattern = smoothstep(-0.01, 0.01, stripe);
    vec3 color = vec3(0.0, 1.0, 0.0);
    fragColor = vec4(color * stripePattern,1.);

}
